import React, {useState} from 'react'
import './ExpenseForm.css'

function ExpenseForm(){
    const [enteredTitle, setEnterdTitle] = useState('')
    const [enteredAmount, setEnterdAmount] = useState('')
    const [enteredDate, setEnterdDate] = useState('')


    const titleChangeHendler= (event)=>{
        setEnterdTitle(event.target.value)
    }
    const amountChangeHendler= (event)=>{
        setEnterdTitle(event.target.value)
    }
    const dateChangeHendler= (event)=>{
        setEnterdTitle(event.target.value)
    }

    return (
        <form>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHendler}/>
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min="0.01" step="0.01" onChange={amountChangeHendler}/>
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min="20219-01-01" max="2021-12-31" onChange={dateChangeHendler}/>
                </div>
            </div>
            <div className='new-expense__actions'>
                <button type="submit">Add Expense</button>
            </div>

        </form>  
    )
}

export default ExpenseForm;
