import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import React, {useState} from "react";

import './Expenses.css'
import Card from "../UI/Card";
function Expenses(props){
    const [filteredYear, setFilteredYear]= useState('2020')
    const filterChangeHendler = selectedYear=>{
        setFilteredYear(selectedYear)
    }
    return (
        <Card className="expenses">
            <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHendler}></ExpensesFilter>
            {(props.expenses.map(expense=> <ExpenseItem expense/>))}
            <ExpenseItem expense={props.expenses[0]}></ExpenseItem>
            <ExpenseItem expense={props.expenses[1]}></ExpenseItem>
            <ExpenseItem expense={props.expenses[2]}></ExpenseItem>
            <ExpenseItem expense={props.expenses[3]}></ExpenseItem>
        </Card>
    )
}

export default Expenses;
